mapper.update:
UPDATE board_config
SET board_en_name = #{boardEnName},
    board_kr_name = #{boardKrName},
    category_id   = #{categoryId},
    board_desc    = #{boardDesc}
where board_en_name = #{boardEnName}

mapper.select:





























SELECT ROW_NUMBER() OVER (ORDER BY a.idx) rownum, a.* from board_config as a


        AND ${searchField} like '%' #{searchValue} '%'


        AND date(reg_date) >= #{startDate}


        AND date(reg_date) <= #{endDate}


order by a.idx desc

    limit #{startRowNo}, #{pageSize}

SELECT count(*) FROM board_config


        AND ${searchField} like '%' #{searchValue} '%'


        AND date(reg_date) >= #{startDate}


        AND date(reg_date) <= #{endDate}

mapper.update:
UPDATE board_category
SET category_en_name = #{categoryEnName} category_kr_name = #{categoryKrName}
    is_active = #{isActive}
where category_id = #{categoryId}

mapper.select:
select * from board_category


        AND category_id = #{categoryId}


        AND board_en_name = #{boardEnName}

mapper.update:
UPDATE board_content
SET title            = #{title},
    content          = #{content},
    category_code_id = #{categoryCodeId},
    is_notice        = #{isNotice}
where content_id = #{contentId}

UPDATE board_content
SET status = 'D',
    where content_id = #{contentId}

UPDATE board_content
set view_cnt = view_cnt + 1
where content_id = #{contentId}

mapper.select:
SELECT count(*) FROM board_content AS a
left join board_category_code AS b
ON a.category_code_id = b.category_code_id
left join users AS c
ON a.writer_id = c.user_id


        AND a.board_en_name = #{boardEnName}


        AND category_id = #{categoryId}


        AND b.category_code_id = #{categoryCodeId}


        AND a.${searchField} like '%' #{searchValue} '%'


        AND date(reg_date) >= #{startDate}


        AND date(reg_date) <= #{endDate}

select *
from (
select null as rownum, a.*, b.category_code_name, b.color, c.user_name
FROM board_content AS a
left join board_category_code AS b
ON a.category_code_id = b.category_code_id
left join users AS c
ON a.writer_id = c.user_id

       and is_notice = true

        AND board_en_name = #{boardEnName}


        AND category_id = #{categoryId}


        AND b.category_code_id = #{categoryCodeId}


        AND ${searchField} like '%' #{searchValue} '%'


        AND date(reg_date) >= #{startDate}


        AND date(reg_date) <= #{endDate}


UNION ALL
SELECT ROW_NUMBER() OVER (ORDER BY a.content_id) rownum,
a.*,
b.category_code_name,
b.color,
c.user_name
FROM board_content AS a
left join board_category_code AS b
ON a.category_code_id = b.category_code_id
left join users AS c
ON a.writer_id = c.user_id

    and is_notice = false

        AND board_en_name = #{boardEnName}


        AND category_id = #{categoryId}


        AND b.category_code_id = #{categoryCodeId}


        AND ${searchField} like '%' #{searchValue} '%'


        AND date(reg_date) >= #{startDate}


        AND date(reg_date) <= #{endDate}


order by content_id desc

    limit #{startRowNo}, #{pageSize}

) as m order by is_notice desc

SELECT * FROM board_content

    AND board_en_name = #{boardEnName}
    and content_id in (
    select * from (
    select content_id from board_content where content_id < #{contentId} order by content_id desc limit 1
    ) as t
    );

SELECT * FROM board_content

    AND board_en_name = #{boardEnName}
    and content_id in (
    select * from (
    select content_id from board_content where content_id > #{contentId} order by content_id limit 1
    ) as t
    );

SELECT a.*, u.user_name, (select user_name from board_reply as r left join users on users.user_id = r.reply_writer where reply_id = a.parent_reply_id) as to_name FROM board_reply as a LEFT JOIN users as u on u.user_id = a.reply_writer
AND content_id = #{contentId}
ORDER BY IF(ISNULL(parent_id), reply_id, parent_id), parent_reply_id

mapper.update:
UPDATE board_reply
SET reply_content = #{replyContent},
    reg_date      = now()
WHERE reply_id = #{replyId}
  AND reply_writer = #{replyWriter}

UPDATE board_reply
SET status = 'D'
WHERE reply_id = #{replyId}
  AND reply_writer = #{replyWriter}
