mapper.update:
UPDATE startup_calendar
set view_cnt = view_cnt + 1
where startup_calendar_id = #{startupCalendarId}

mapper.select:
SELECT * FROM startup_calendar

    and startup_calendar_id in (
    select * from (
    select startup_calendar_id from startup_calendar where startup_calendar_id < #{startupCalendarId} order by startup_calendar_id desc limit 1
    ) as t
    );

SELECT * FROM startup_calendar

    and startup_calendar_id in (
    select * from (
    select startup_calendar_id from startup_calendar where startup_calendar_id > #{startupCalendarId} order by startup_calendar_id limit 1
    ) as t
    );

SELECT ROW_NUMBER() OVER (ORDER BY a.startup_calendar_id) rownum, a.*, c.category_code_name from startup_calendar as a
LEFT JOIN startup_calendar_category_code as c
ON c.category_code_id = a.category_code_id


        AND a.category_code_id = #{categoryCodeId}


        AND ${searchField} like '%' #{searchValue} '%'


        AND DATE_FORMAT(a.apply_start_date, '%Y-%m') = #{date}
        AND DATE_FORMAT(a.apply_end_date, '%Y-%m') = #{date}
        AND DATE_FORMAT(a.event_date, '%Y-%m') = #{date}



        AND DATE_FORMAT(a.apply_start_date, '%Y-%m') = #{date}
        AND DATE_FORMAT(a.apply_end_date, '%Y-%m') = #{date}
        AND DATE_FORMAT(a.event_date, '%Y-%m') = #{date}



        and a.event_date BETWEEN #{startDate} AND #{endDate}


order by a.reg_date desc


    limit #{startRowNo}, #{pageSize}

SELECT count(*) from startup_calendar as a


        AND a.category_code_id = #{categoryCodeId}


        AND ${searchField} like '%' #{searchValue} '%'


        and a.event_date BETWEEN #{startDate} AND #{endDate}
